function authFunction(uname, password, $http, $rootScope, success, error) {

  var authSuccess = success;
  var authError = error;



  //Banner Authentication with LDAP - middlware auth

  //var url = "https://kryptosmw.kryptosmobile.com/webadapter1/services/authenticate/NSC/PS";
  var url = "https://kryptosmw.kryptosmobile.com/webadapter2/services/authenticate/TMCCTEST/PS9";

  var data = {};
  if (window.device) {
    data = "username=" + uname + "&password=" + password;
  } else {
    url = "/websimulator/json?url=" + url;
    data = {
      method: "POST",
      body: "username=" + uname + "&password=" + password
    };
  }
  var authUrl = url;
  var authData = data;
  $rootScope.generateST = function(tgt, service, callback) {
    //Bypassing the ST as there is no service ticket required here..
    callback(tgt);
  };

  $rootScope.postAPI = function(endpoint, postdata, callback) {
    $.blockUI();
    $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
      var serviceurl = endpoint + "?ticket=" + st;
      var url = "";
      var proxyMethod = "POST";
      var proxyData = postdata;
      if (window.device) {
        url = serviceurl;
      } else {
        url = "/websimulator/json?url=" + serviceurl;
        proxyMethod = "POST";
        proxyData = {
          method: "POST",
          body: postdata
        }
      }
      $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      }).
      success(function(data, status, headers, config) {
        $.unblockUI();
        callback(data, status, headers, config);
      }).
      error(function(data, status, headers, config) {
        $.unblockUI();
        alert("Error in service call : " + data + " Status : " + status);
        if (status == 403) { //Ticket expired.. need to relogin
          $http.post(authUrl, authData).success(function(data, status, headers, config) {
            authSuccess(data);
            $rootScope.postAPI(endpoint, postdata, callback);
          }).error(function(data, status, headers, config) {
            authError(data);

          });
        }

      });

    });

  };

  $rootScope.getAPI = function(endpoint, callback) {
    $.blockUI();

    $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
      var serviceurl = endpoint + "?ticket=" + st;
      var url = "";
      var proxyMethod = "GET";
      var proxyData = {};
      if (window.device) {
        url = serviceurl;
      } else {
        url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
        proxyMethod = "POST";
        proxyData = {
          method: "GET"
        }
      }
      $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      }).
      success(function(data, status, headers, config) {
        $.unblockUI();
        callback(data, status, headers, config);
      }).
      error(function(data, status, headers, config) {
        $.unblockUI();
        alert("Error in service call : " + data + " Status : " + status);
        if (status == 403) { //Ticket expired.. need to relogin
          $http.post(authUrl, authData).success(function(data, status, headers, config) {
            authSuccess(data);
            $rootScope.getAPI(endpoint, callback);
          }).error(function(data, status, headers, config) {
            authError(data);

          });
        }

      });

    });

  };




  $.blockUI({
    message: '<div style="margin: 2px; vertical-align: middle; display: inline-block;position:fixed;left:0px;right:0px;margin:auto; color:#fff;"><i class="icon-cog icon-spin icon-4x"></i><h3>Authenticating</h3></div>'
  });

  $rootScope.logoutcb = function() {
    $.jStorage.deleteKey('profileName');
    $.jStorage.deleteKey("profilePic");
    MyCampusApp.rootScope.userProfileName = "";
    MyCampusApp.rootScope.userProfilePic = "";
    //$route.reload();
  }

  $http.post(url, data).success(function(data, status, headers, config) {
    success(data);

    if (!data.error) {
      $rootScope.profileShow = true;
      $rootScope.getAPI($rootScope.middlewareServerUrl + "/psdemographics", function(profileData) {

        var proName = profileData.PERSON_NAME[0].NAME;
        $rootScope.userProfileName = proName;
        $ootScope.userProfilePic = "images/profileImage.png";

      });
    }


    $.unblockUI();
  }).error(function(data, status, headers, config) {
    error(data);
    $.unblockUI();
  });

  $.blockUI({
    message: '<div style="margin: 2px; vertical-align: middle; display: inline-block"><i class="icon-cog icon-spin icon-4x"></i><h3 style="color:#fff;">Authenticating</h3></div>'
  });

  $http.post(url, data).success(function(data, status, headers, config) {
    data.roles = ["student"];
    success(data);
    $.unblockUI();
  }).error(function(data, status, headers, config) {
    error(data);
    $.unblockUI();
  });

  /*Added by Nick- for Login go button - start*/
  $('#loginForm').on('submit', function(event) {
    event.preventDefault();
    $scope.login();
    $scope.$digest();
    return false;
  });
  /*end*/
}

